// Import library
import React, { useState } from "react";
import {
    BrowserRouter,
    Routes,
    Route
} from "react-router-dom";
// Import file
import "./App.scss";
// Import component
import UserHome from './user/Home/Home';
import UserHeader from './user/Navbar/Navbar';
import UserFooter from './user/Footer/Footer';

function App() { 
    const [account_detail, setAccountDetail] = useState({
        // Default: normal
        status: 'normal', // ban - normal
        // Default: anonymous
        type: 'user' // admin - user - anonymous 
        // type and status only change when login
    })
    return (
        <React.Fragment>
            <BrowserRouter>
                <div className='bg-eee'>
                    <Routes>
                        {/* If not login */}
                        {/* { account_detail.type === 'anonymous' && 
                            <>
                                <Route
                                    exact path={"/"}
                                    element={
                                        <Navigate
                                            to="/login"
                                        />
                                    }
                                /> 
                            </> 
                        } */}

                        {/* If login by using user account */}
                        { account_detail.type === 'user' && 
                            <>
                                <Route
                                    exact path={"/"}
                                    element={
                                        <>
                                            <UserHeader/>
                                            <UserHome/>
                                            <UserFooter/>
                                        </>
                                    }
                                /> 
                            </> 
                        }

                        {/* If login by using admin account */}
                        {/* { account_detail.type === 'admin' && 
                            <>
                                <Route
                                    exact path={"/"}
                                    element={
                                        <Navigate
                                            to="/login"
                                        />
                                    }
                                />
                            </> 
                        } */}
                    </Routes>
                </div>
            </BrowserRouter>    
        </React.Fragment>
    );
}

export default App;
